		// TODO Auto-generated method stub
		
		// final sempre use  MAIUSCULOS
		final int CONSTANTE = 552222222;   //<< mais usado
		byte num = 122; // 127
		short num2 = 30000; //
		long num3 = 2333887387676887682L; // acita numeros muito grandes coloque L no final
		float flo = 0.2F; // Coloque F no final
